mapper.update:
UPDATE CAR_SPEC
SET use_yn = 'N', deleted_at = NOW()
WHERE spec_id = #{specId}

mapper.select:
SELECT 
FROM CAR_SPEC
WHERE brand = #{brand}
AND model_name = #{modelName}
AND model_year_base = #{modelYearBase}
AND use_yn = 'N'

mapper.update:
UPDATE CAR_SPEC
SET use_yn = 'Y', deleted_at = NULL
WHERE spec_id = #{specId}
