mapper.select:
SELECT /* CAR_SPEC */ cs.spec_id AS specId, cs.model_name AS modelName, cs.display_name_short AS displayNameShort, cs.car_class AS carClass, cs.fuel_type AS fuelType, cs.seating_capacity AS seatingCapacity, cs.model_year_base AS modelYearBase, cs.main_video_url AS mainVideoUrl, cs.min_driver_age AS minDriverAge, cs.min_license_years AS minLicenseYears, cs.fuel_efficiency AS fuelEfficiency,
/* BRANCH (다이어트: 대표 지점 1개만 붙임 - 가격정책의 branch 기준) */ b.branch_id AS branchId, b.branch_name AS branchName, b.address_basic AS addressBasic, b.latitude AS latitude, b.longitude AS longitude, b.open_time AS openTime, b.close_time AS closeTime,
/* PRICE_POLICY (DAILY) */ pp.base_price AS dailyPrice, pp.discount_rate AS discountRate
FROM CAR_SPEC cs /* 가격정책이 있어야 branch를 알 수 있으니 PRICE_POLICY를 기준으로 조인 */ LEFT JOIN PRICE_POLICY pp ON pp.spec_id = cs.spec_id AND pp.price_type = 'DAILY' AND pp.is_active = TRUE AND pp.use_yn = 'Y'
LEFT JOIN BRANCH b ON b.branch_id = pp.branch_id
WHERE cs.spec_id = #{specId} AND cs.use_yn = 'Y'
/* branch_id가 있는 정책을 우선 + 최신 valid_from 우선 */ ORDER BY (pp.branch_id IS NULL) ASC, pp.valid_from DESC LIMIT 1
