mapper.select:
SELECT id, category, question, answer FROM faq
category = #{category}
AND ( question LIKE CONCAT('%', #{keyword}, '%') OR answer LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM faq
category = #{category}
AND ( question LIKE CONCAT('%', #{keyword}, '%') OR answer LIKE CONCAT('%', #{keyword}, '%') )

SELECT id, category, question, created_at AS createdAt FROM faq
category = #{category}
AND ( question LIKE CONCAT('%', #{keyword}, '%') OR answer LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM faq
category = #{category}
AND ( question LIKE CONCAT('%', #{keyword}, '%') OR answer LIKE CONCAT('%', #{keyword}, '%') )
