mapper.select:
SELECT id, reservation_id as reservationId, car_name as carName, rating, content, period, DATE_FORMAT(created_at, '%Y-%m-%d') as createdAt
FROM reviews
WHERE id = #{reviewId}

SELECT id, reservation_id as reservationId, car_name as carName, rating, content, period, DATE_FORMAT(created_at, '%Y-%m-%d') as createdAt
FROM reviews
WHERE id = #{reviewId} AND user_id = #{userId}

SELECT id, reservation_id as reservationId, car_name as carName, rating, content, period, DATE_FORMAT(created_at, '%Y-%m-%d') as createdAt
FROM reviews
WHERE reservation_id = #{reservationId}

mapper.update:
UPDATE reviews
SET rating = #{rating}, content = #{content}, updated_at = CURRENT_TIMESTAMP
WHERE id = #{reviewId}

mapper.select:
SELECT id, reservation_id as reservationId, spec_id, user_id, car_name as carName, rating, content, period, DATE_FORMAT(created_at, '%Y-%m-%d') as createdAt
FROM reviews
WHERE spec_id = #{specId}
ORDER BY created_at DESC
    LIMIT #{limit}
