mapper.select:
SELECT *
FROM users
WHERE provider = #{provider}
AND provider_id = #{providerId}
AND deleted_at IS NULL

SELECT COUNT(*)
FROM users
WHERE provider = #{provider}
AND provider_id = #{providerId}
AND deleted_at IS NULL

SELECT *
FROM users
WHERE provider = #{provider}
AND provider_id = #{providerId}
AND deleted_at IS NOT NULL

mapper.update:
UPDATE users
SET password = #{password}
WHERE user_id = #{userId}
AND provider = #{provider}
AND deleted_at IS NULL

UPDATE users
SET membership_grade = #{membershipGrade}
WHERE user_id = #{userId}
AND deleted_at IS NULL

DELETE FROM users
WHERE user_id = #{userId}
AND provider != 'LOCAL'
