mapper.select:
SELECT *
FROM users
WHERE provider = #{provider}
AND provider_id = #{providerId}
AND deleted_at IS NULL

SELECT COUNT(*)
FROM users
WHERE provider = #{provider}
AND provider_id = #{providerId}
AND deleted_at IS NULL

mapper.update:
UPDATE users
SET password_hash = #{password}
WHERE user_id = #{userId}
AND provider =#{provider}
AND deleted_at IS NULL

UPDATE users
SET membership_grade = #{membershipGrade}
WHERE user_id = #{userId}
AND deleted_at IS NULL
